mapper.update:
update role
set isDelete = 1 and modifyTime = now()
where keyId = #{keyId,jdbcType=BIGINT}

update role
set isDelete = 1 and modifyTime = now()
where keyId IN #{roleId}

mapper.select:
select keyId, roleName, addTime, modifyTime, isDelete
from role
where isDelete = 0 and keyId IN  #{roleId}
