mapper.insert:
insert into roleresource (keyId, roleId, resourceId
  )
values (#{keyId,jdbcType=BIGINT}, #{roleId,jdbcType=BIGINT}, #{resourceId,jdbcType=BIGINT}
  )

mapper.update:
update roleresource
set roleId = #{roleId,jdbcType=BIGINT},
  resourceId = #{resourceId,jdbcType=BIGINT}
where keyId = #{keyId,jdbcType=BIGINT}

mapper.select:
select keyId, roleId, resourceId
from roleresource
where keyId = #{keyId,jdbcType=BIGINT}
